import SidebarMenu from '../components/SidebarMenu.vue';
export default {
  name: 'AccountInfo',
  components: {
    'side': SidebarMenu,
    FormHint: () => import('../components/FormHint.vue'),
    AccountInfoAccountProgress: () => import('../components/AccountInfoAccountProgress.vue')
  },

  data() {
    return {
      progress: Number(localStorage.getItem('accountInfoProgress')) || 0,
      hint: ''
    };
  },

  methods: {
    updateProgress(progress) {
      this.progress = progress;
      localStorage.setItem('accountInfoProgress', progress);
    },

    updateHint() {
      let hint = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : '';
      this.hint = hint;
    },

    goToForm(routeName) {
      this.$router.push({
        name: routeName
      });
      window.scrollTo(0, 0);
    }

  }
};
