import { API } from '../api.js';
import { isMobile } from "mobile-device-detect";
export default {
  name: 'HomeView',

  mounted() {
    this.msg = isMobile ? true : false;
    console.log("isMobile", this.msg);
  },

  data() {
    return {
      keyword: process.env.VUE_APP_GET_STARTED_DOT || '',
      companies: [],
      company: null,
      msg: false,
      noData: false,
      loading: false,
      error: null,
      localUsdot: '',
      localcompany: ''
    };
  },

  methods: {
    getStarted() {
      var _this = this;

      return _asyncToGenerator(function* () {
        if (!_this.keyword) {
          return;
        }

        _this.loading = true;
        _this.error = null;
        _this.companies = [];
        _this.company = null;
        _this.noData = false;

        try {
          let data = yield API.get('company/search', {
            keyword: _this.keyword
          });

          if (Array.isArray(data.data)) {
            _this.companies = data.data;
            _this.noData = !_this.companies.length; // console.log("companies",this.companies)
          } else {
            _this.company = data.data;
            _this.noData = !Object.keys(_this.company).length;
          }
        } catch (err) {
          _this.error = err.message;
        } finally {
          _this.loading = false;
        }
      })();
    },

    createCompany(usdot, name) {
      var _this2 = this;

      return _asyncToGenerator(function* () {
        _this2.localUsdot = usdot;
        _this2.localcompany = name;
        localStorage.setItem("usdot", _this2.localUsdot);
        localStorage.setItem("company", _this2.localcompany); // console.log("this.localUsdot",this.localUsdot)

        _this2.loading = true;

        try {
          let data = yield API.post('company/create', null, {
            usdot
          });
          console.log("data", data);
          localStorage.setItem("Physical address", data.data["Physical Address"]);
          localStorage.setItem("Mailing address", data.data["Mailing Address"]);
          localStorage.setItem("Phone", data.data.Phone);
          localStorage.setItem("accBtn", false);

          if (data.status === 'OK') {
            if (_this2.msg) {
              _this2.$router.push({
                name: 'AccountInfoUploadDocuments'
              });
            } else {
              _this2.$router.push({
                name: 'AccountInfoPersonalInfo'
              });
            }
          } else if (data.status === 'ERROR') {
            _this2.error = data.messages[0] || data.data;
          }
        } catch (err) {
          console.error(err);
          _this2.error = err.message;
        } finally {
          _this2.loading = false;
        }
      })();
    }

  }
};
