import { validateField, validateForm, required } from '../validators.js';

export default {
  name: 'AccountInfoOwnersItem',
  props: {
    index: {
      type: Number,
      required: true
    },
    owner: {
      type: Object,
      required: false,
      default() {
        return {};
      }
    }
  },
  data() {
    return {
      formData: {
        firstName: '',
        lastName: '',
        dobM: '',
        dobD: '',
        dobY: '',
        address: '',
        state: ''
      },
      rules: {
        firstName: [required],
        lastName: [required],
        dobM: [required],
        dobD: [required],
        dobY: [required],
        address: [required],
        state: [required]
      },
      formErrors: {},
      hints: {},
      loading: false,
      error: null
    };
  },
  computed: {
    dateOfBirth() {
      let { dobM: m, dobD: d, dobY: y } = this.formData;

      if (m.length < 2) {
        m = `0${m}`;
      }

      if (d.length < 2) {
        d = `0${d}`;
      }

      return `${m}/${d}/${y}`;
    }
  },
  created() {
    let dobM = '',
      dobD = '',
      dobY = '';

    if (this.owner.dateOfBirth) {
      [dobM, dobD, dobY] = this.owner.dateOfBirth.split('/');
    }

    this.formData = {
      ...this.formData,
      ...this.owner,
      dobM,
      dobD,
      dobY
    };
  },
  methods: {
    getFormData() {
      let formData = {
        ...this.formData,
        dateOfBirth: this.dateOfBirth
      };

      delete formData.dobM;
      delete formData.dobD;
      delete formData.dobY;

      return formData;
    },
    removeForm(index) {
      this.$emit('remove-form', index);
    },
    onFocus(fieldName) {
      this.$emit('update-hint', this.hints[fieldName]);
    },
    onBlur() {
      this.$emit('update-hint', '');
    },
    validateField(fieldName) {
      validateField(fieldName, this.formData, this.rules, this.formErrors);
    },
    validateForm() {
      this.formErrors = {};
      return validateForm(this.formData, this.rules, this.formErrors);
    }
  }
};
