// import { validateField, validateForm, required } from '../validators.js';
import { API } from '../api.js';
import { isMobile } from 'mobile-device-detect';

export default {
  name: 'AccountInfoUploadDocuments',
  mobile:'phone',
  props: {
    nextForm: {
      type: String,
      required: true
    },
    progress: {
      type: Number,
      required: true
    }
  },
  data() {
    return {
      msg: isMobile ? 'Welcome to Your Vue.js mobile App!' : 'Welcome to Your Vue.js App',
      formData: {
        imageIdFront: null,
        imageIdBack: null,
        imageDOT: null,
        imageRegistration: null
      },
      // rules: {},
      formErrors: {},
      preview: {
        imageIdFront: null,
        imageIdBack: null,
        imageDOT: null,
        imageRegistration: null
      },
      loading: false,
      error: null
    };
  },
  computed: {
    submitBtnText() {
      if (this.loading) {
        return 'Loading...';
      }

      let {
        imageIdFront,
        imageIdBack,
        imageDOT,
        imageRegistration
      } = this.formData;
      let anyImageSelected =
        imageIdFront || imageIdBack || imageDOT || imageRegistration;
      if (anyImageSelected) {
        return 'Upload';
      }

      return 'Skip';
    }
  },
  created() {
    this.$emit('update-progress', this.progress);
  },
  methods: {
    // validateField(fieldName) {
    //   validateField(fieldName, this.formData, this.rules, this.formErrors);
    // },
    // validateForm() {
    //   this.formErrors = {};
    //   return validateForm(this.formData, this.rules, this.formErrors);
    // },
    setImage(event, fieldName) {
      let { files } = event.target;

      let image = files[0] || null;

      this.formData[fieldName] = image;
      this.previewFile(image, fieldName);

      // this.validateField(fieldName);
    },
    previewFile(file, fieldName) {
      let reader = new FileReader();

      reader.onloadend = () => {
        this.preview[fieldName] = reader.result;
      };

      if (file) {
        reader.readAsDataURL(file);
      } else {
        this.preview[fieldName] = null;
      }
    },
    async uploadDocuments() {
      // let formIsValid = this.validateForm();
      // if (!formIsValid) {
      //   return;
      // }


      try {
        let data = await API.formData('company/upload', this.formData);

        // console.log(data);

        if (data.status === 'OK') {
          this.$router.push({ name: this.nextForm });
        } else if (data.status === 'ERROR') {
          this.error = data.messages[0] || data.data;
        }
      } catch (err) {
        console.error(err);
        this.error = err.message;
      } finally {
        this.loading = false;
      }
    }
  }
};
