import { mapState } from 'vuex';
import { validateField, validateForm, minLength } from '../validators.js';
import { API } from '../api.js';
import ModalLogin from "./ModalLogin.vue"


export default {
  name: 'AccountInfoEldProvider',
  props: {
    prevForm: {
      type: String,
      required: true
    },
    nextForm: {
      type: String,
      required: true
    },
    progress: {
      type: Number,
      required: true
    }
  },
  data() {
    return {
       showmodel:false,
      formData: {
        eldProvider: []
      },
      rules: {
        eldProvider: [val => minLength(val, 1, 'Please select ELD Provider')]
      },
      formErrors: {},
      hints: {},
      userProviders: [],
      userProviderName: '',
      loading: false,
      error: null,
      save:true
    };
  },
  computed: {
    ...mapState('eld', ['eldProviders']),
    allProviders() {
      return this.eldProviders.concat(this.userProviders);
    },
    eldProviderMap() {
      let map = {};
      this.formData.eldProvider.forEach(val => (map[val] = true));
      return map;
    },
      ...mapState([
    'data'
  ])
  },
  created() {
    this.$emit('update-progress', this.progress);
    this.loadCompany();
  }, components:{
    "modelLogin":ModalLogin
  },
 mounted(){
    if(localStorage.getItem("token")){
      this.$store.dispatch('loadData',localStorage.getItem("uuid"))
     let a = this.$store.state.getData.data[6]
     let b = JSON.parse(a.val).eldProvider
     console.log(b)
    let c = [];
    for(var i=0;i<b.length;i++){
        c.push(b[i])
     }
     let filteredKeywords = c.filter((word)=> b.includes(word));
    console.log("filteredKeywords",filteredKeywords);
    for(var i=0;i<filteredKeywords.length;i++){
     this.selectProvider(filteredKeywords[i]);
    }
     this.save = false
   }else{
     this.save = true
   }
 },
updated(){
if(localStorage.getItem("showModal") == "true")
     {
       this.showmodel = true;
     }
     else{
       this.showmodel = false;
     }
 },
  methods: {

    async show() {
      let formIsValid = this.validateForm();
      if (!formIsValid) {
        return;
      }

      this.loading = true;
      this.error = null;

      try {
        let data = await API.post("company/save", {
          key: "eldProvider",
          val: this.formData,
          userId:localStorage.getItem("userId"),
          uuid:localStorage.getItem("uuid")
        });

        if (data.status === "OK") {
            if(this.showmodel){
          this.showmodel = false;

        }else{
          this.showmodel = true;
        }

        } else if (data.status === "ERROR") {
          this.showmodel = true;
          this.error = data.messages[0] || data.data;
        }
      } catch (err) {
        console.error(err);
        // this.showmodel = true;
        this.error = err.message;
      } finally {
        // this.showmodel = true;
        this.loading = false;
      }
    },
    addProvider() {
      if (
        !this.userProviderName ||
        this.eldProviderMap[this.userProviderName]
      ) {
        return;
      }

      this.userProviders.push({ value: this.userProviderName });
      this.userProviderName = '';
    },
    selectProvider(providerValue) {
      if (this.eldProviderMap[providerValue]) {
        this.formData.eldProvider = this.formData.eldProvider.filter(
          val => val !== providerValue
        );
        this.$emit('update-hint', '');
      } else {
        this.formData.eldProvider.push(providerValue);
        this.$emit('update-hint', this.hints[providerValue]);
      }

      this.formErrors = {};
    },
    goPrevForm() {
      this.$emit('update-hint', '');
      this.$emit('go-to-form', this.prevForm);
    },
    goNextForm() {
      this.$emit('update-hint', '');
      this.$emit('go-to-form', this.nextForm);
    },
    validateField(fieldName) {
      validateField(fieldName, this.formData, this.rules, this.formErrors);
    },
    validateForm() {
      this.formErrors = {};
      return validateForm(this.formData, this.rules, this.formErrors);
    },
    async loadCompany() {
      this.loading = true;
      this.error = null;

      try {
        let data = await API.get('company/current');

        if (data.status === 'OK') {
          let { eldProvider: eldProviderTab } = data.data;
          if (eldProviderTab) {
            this.formData = {
              ...this.formData,
              ...eldProviderTab
            };

            let { eldProvider } = eldProviderTab;
            this.addUserProviders(eldProvider);
          }
        } else if (data.status === 'ERROR') {
          // this.$router.replace({ name: 'Home' });
        }
      } catch (err) {
        console.error(err);
        this.error = err.message;
      } finally {
        this.loading = false;
      }
    },
    addUserProviders(eldProvider) {
      eldProvider.forEach(providerName => {
        let providersIndex = this.eldProviders.findIndex(
          p => p.value === providerName
        );
        if (providersIndex === -1) {
          this.userProviders.push({ value: providerName });
        }
      });
    },
    async updateCompany() {
      let formIsValid = this.validateForm();
      if (!formIsValid) {
        return;
      }

      this.loading = true;
      this.error = null;

      try {
        let data = await API.post('company/save', {
          key: 'eldProvider',
          val: this.formData,
          userId:localStorage.getItem("userId"),
          uuid:localStorage.getItem("uuid")
        });

        if (data.status === 'OK') {
          this.goNextForm();
        } else if (data.status === 'ERROR') {
          this.error = data.messages[0] || data.data;
        }
      } catch (err) {
        console.error(err);
        this.error = err.message;
      } finally {
        this.loading = false;
      }
    }
  }
};
