import uuidv4 from 'uuid/v4';
import { API } from '../api.js';

export default {
  name: 'AccountInfoDrivers',
  components: {
    AccountInfoDriversItem: () => import('./AccountInfoDriversItem.vue')
  },
  props: {
    prevForm: {
      type: String,
      required: true
    },
    nextForm: {
      type: String,
      required: true
    },
    progress: {
      type: Number,
      required: true
    }
  },
  data() {
    return {
      drivers: [],
      loading: false,
      error: null
    };
  },
  created() {
    this.$emit('update-progress', this.progress);
    this.loadCompany();
  },
  methods: {
    addForm() {
      this.drivers.push({ _uuid: uuidv4() });
      this.error = null;
    },
    removeForm(index) {
      this.drivers.splice(index, 1);

      if (!this.drivers.length) {
        this.error = 'Please add at least one driver';
      }
    },
    updateHint(hint) {
      this.$emit('update-hint', hint);
    },
    goPrevForm() {
      this.$emit('go-to-form', this.prevForm);
    },
    goNextForm() {
      this.$emit('go-to-form', this.nextForm);
    },
    async loadCompany() {
      this.loading = true;
      this.error = null;

      try {
        let data = await API.get('company/current');

        if (data.status === 'OK') {
          let { drivers } = data.data;

          if (drivers && drivers.length) {
            this.drivers = drivers.map(d => ({ ...d, _uuid: uuidv4() }));
          } else {
            this.addForm();
          }
        } else if (data.status === 'ERROR') {
          this.$router.replace({ name: 'Home' });
        }
      } catch (err) {
        console.error(err);
        this.error = err.message;
      } finally {
        this.loading = false;
      }
    },
    setDataFromForms() {
      let drivers = [];

      this.$refs.driverForm.forEach(formRef => {
        let formData = formRef.getFormData();
        drivers.push(formData);
      });

      this.drivers = drivers;
    },
    async updateCompany() {
      this.setDataFromForms();

      let driverForms = this.$refs.driverForm;
      if (!driverForms.length) {
        return;
      }

      let allFormAreValid = driverForms.every(formRef =>
        formRef.validateForm()
      );

      if (!allFormAreValid) {
        return;
      }

      this.loading = true;
      this.error = null;

      try {
        let data = await API.post('company/save', {
          key: 'drivers',
          val: this.drivers.map(d => {
            let driver = { ...d };
            delete driver._uuid;
            return driver;
          })
        });

        if (data.status === 'OK') {
          this.goNextForm();
        } else if (data.status === 'ERROR') {
          this.error = data.messages[0] || data.data;
        }
      } catch (err) {
        console.error(err);
        this.error = err.message;
      } finally {
        this.loading = false;
      }
    }
  }
};
