import { validateField, validateForm, required } from "../validators.js";
import { API } from "../api.js";
import ChatBoat from "./ChatBoat.vue";

export default {
  name: "AccountInfoPersonalInfo",
  components: {
    "chat-boat": ChatBoat
  },
  props: {
    nextForm: {
      type: String,
      required: true
    },
    progress: {
      type: Number,
      required: true
    }
  },
  mounted() {
    this.formData.USDOT = localStorage.getItem("usdot");
    this.formData.company = localStorage.getItem("company");
    console.log("this.formData.address",this.formData.address)
    this.formData.address = localStorage.getItem(["Physical address"]);
    console.log("this.formData.address",this.formData.address)
    this.formData.phone = localStorage.getItem("Phone");

  },
  data() {
    return {
      checked: true,
      formData: {
        // firstName: "",
        // lastName: "",
        address: "",
        city: "",
        state: "",
        zip: "",
        USDOT: "",
        company:'',
        phone:'',
        address1:"",
        city1:"",
        state1:"",
        zip1:""
      },
      rules: {
        // firstName: [required],
        // lastName: [required],
        address: [required],
        city: [required],
        state: [required],
        zip: [required]
      },
      formErrors: {},
      hints: {

        address: "Another hint"
      },
      loading: false,
      error: null
    };
  },
  created() {
    this.$emit("update-progress", this.progress);
    this.loadCompany();

  },
  methods: {
    onFocus(fieldName) {
      this.$emit("update-hint", this.hints[fieldName]);
    },
    onBlur() {
      this.$emit("update-hint", "");
    },
    goNextForm() {
      this.$emit("go-to-form", this.nextForm);
    },
    validateField(fieldName) {
      validateField(fieldName, this.formData, this.rules, this.formErrors);
    },
    validateForm() {
      this.formErrors = {};
      return validateForm(this.formData, this.rules, this.formErrors);
    },
    async loadCompany() {
      this.loading = true;
      this.error = null;

      try {
        let data = await API.get("company/current");

        if (data.status === "OK") {
          let { personalInfo } = data.data;
          if (personalInfo) {
            this.formData = {
              ...this.formData,
              ...personalInfo
            };
          }
        } else if (data.status === "ERROR") {
          this.$router.replace({ name: "Home" });
        }
      } catch (err) {
        console.error(err);
        this.error = err.message;
      } finally {
        this.loading = false;
      }
    },
    async updateCompany() {
      let formIsValid = this.validateForm();
      if (!formIsValid) {
        return;
      }

      this.loading = true;
      this.error = null;

      try {
        let data = await API.post("company/save", {
          key: "personalInfo",
          val: this.formData
        });

        if (data.status === "OK") {
          this.goNextForm();
        } else if (data.status === "ERROR") {
          // console.log("data",data)
          this.error = data.messages[0] || data.data;
        }
      } catch (err) {
        // console.error(err);
        this.error = err.message;
      } finally {
        this.loading = false;
      }
    }
  }
};
