import { API } from "../api.js";

export default {
  name: "AccountInfoDocumentUpload",
  props: {
    prevForm: {
      type: String,
      required: true
    },
    nextForm: {
      type: String,
      required: true
    },
    progress: {
      type: Number,
      required: true
    }
  },
  data() {
    return {
      formData: {
        lossRun: null,
        ifta: null,
        contracts: null,
        declarations: null,
        rentalLeaseAgreement: null,
        previouslyCompletedApplications: null,
        insuranceRequirements: null
      },
      formErrors: {},
      loading: false,
      error: null
    };
  },
  created() {
    this.$emit("update-progress", this.progress);
    this.loadCompany();
  },
  methods: {
    goPrevForm() {
      this.$emit("go-to-form", this.prevForm);
    },
    goNextForm() {
      this.$emit("go-to-form", this.nextForm);
    },
    onFileChange(event, fieldName) {
      let file = event.target.files[0] || null;
      this.formData[fieldName] = file;
    },
    async loadCompany() {
      this.loading = true;
      this.error = null;

      try {
        let data = await API.get("company/current");

        if (data.status === "ERROR") {
          this.$router.replace({ name: "Home" });
        }
      } catch (err) {
        console.error(err);
        this.error = err.message;
      } finally {
        this.loading = false;
      }
    },
    async updateCompany() {
      this.loading = true;
      this.error = null;

      try {
        let data = await API.formData("company/upload", this.formData);

        if (data.status === "OK") {
          this.goNextForm();
        } else if (data.status === "ERROR") {
          this.error = data.messages[0] || data.data;
        }
      } catch (err) {
        console.error(err);
        this.error = err.message;
      } finally {
        this.loading = false;
      }
    }
  }
};
