import dayjs from 'dayjs';
import { API } from '../api.js';

export default {
  name: 'AdminDocumentsItem',
  components: {
    DocumentPreview: () =>
      import(/* webpackChunkName: "DocumentPreview" */
      '../components/DocumentPreview.vue')
  },
  filters: {
    date(str) {
      return dayjs(str).format('MMM DD, YYYY');
    },
    datetime(str) {
      return dayjs(str).format('MMM DD, YYYY [at] HH:mm');
    }
  },
  props: {
    docId: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      document: null,
      loading: true,
      error: null
    };
  },
  created() {
    this.loadDocument();
  },
  methods: {
    async loadDocument() {
      this.loading = true;
      this.error = null;
      this.document = null;

      try {
        let data = await API.post('documents/admin/item', {
          _id: this.docId
        });

        this.document = data.data;
      } catch (err) {
        this.error = err.message;
      } finally {
        this.loading = false;
      }
    }
  }
};
